cout c endl; //地址0034FBBC
int d=0; b= d;
cout *b endl;//值0
cout b endl; //地址0038FBC0   引用与指针区别   引用必须在定义时初始化，指针可以先声明后初始化。  引用不能有NULL引用，指针可以为NULL  引用一定初始化不能再指向其他对象，而指针可以改变所指对象。  引用不占内存，只是变量的一个别名，指针会在栈区开辟一个4byte的内存区。   图示指针内存分布:   c++ 传值，传址，传引用  void swap(int a,int b);
void swap2(int  amp;x,int  amp;y);
void swap3(int*  a,int* b);
int _tmain(int argc, _TCHAR* argv[])
{

    int a=1,b=2;
    swap(a,b); //传值 ,参数为值拷贝,外部变量不会改变值
    cout a endl; //1
    cout b endl;//2
    int  amp;m= a;
    int   amp;n = b;
    swap2(m,n); //传引用,值改变
    cout a endl; //2
    cout b endl; //1
    int *k=  amp;a;
    int  *j =  amp;b;
    swap3(k,j);//传指针,值改变
    cout a endl;//1
    cout b endl;//2

    return 0;
}

void swap(int a,int b){
    int temp = a;
    a = b;
    b=a;
}
void swap2(int  amp;x,int  amp;y)
{
    int temp;
    temp=x;
    x=y;
    y=temp;
}
void swap3(int*  a,int* b){
    int temp = *a;
    *a = *b;
    *b = temp;
}  java的传值，传引用  public class Test {

